    const testimonials = await getTestimonials();

    if (testimonials.length === 0) {
      return null;
    }

    return (
      <section id="testimonials" className="py-20 bg-surface">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center text-foreground mb-12">Client Testimonials</h2>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {testimonials.map((testimonial) => (
              <div key={testimonial.id} className="bg-background rounded-xl p-6 relative">
                <Quote className="absolute top-4 right-4 w-8 h-8 text-primary/20" />

                <div className="flex items-center gap-4 mb-4">
                  {testimonial.imageUrl && (
                    <div className="relative w-12 h-12">
                      <Image
                        src={testimonial.imageUrl}
                        alt={testimonial.name}
                        fill
                        className="rounded-full object-cover"
                      />
                    </div>
                  )}
                  <div>
                    <h4 className="font-semibold text-foreground">{testimonial.name}</h4>
                    <p className="text-sm text-muted">{testimonial.position}</p>
                  </div>
                </div>

                <div className="flex gap-1 mb-3">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      className={`w-4 h-4 ${
                        i < testimonial.rating
                          ? 'fill-primary text-primary'
                          : 'text-border'
                      }`}
                    />
                  ))}
                </div>

                <p className="text-muted-foreground italic">\u201c{testimonial.text}\u201d</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  } catch (error) {
    return null;
  }
}

