const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001';

export interface AboutProfile {
  name: string;
  title: string;
  bio: string;
  email: string;
  location: string;
  profileImage: string;
}

export interface Project {
  id: number;
  title: string;
  description: string;
  technologies: string[];
  imageUrl: string;
  liveUrl: string;
  githubUrl: string;
  details?: string;
}

export interface SkillCategory {
  id: number;
