import { Suspense } from "react";
import { getAbout, getProjects, getSkills, getBlogPosts, About, Project, Skill, BlogPost } from "./utils/data";
import { ContactForm, SectionHeader } from "./components/Component1";
import { AboutCard, ProjectsGrid, SkillsCloud } from "./components/Component2";
import { BlogList } from "./components/Component3";

function SectionSkeleton(props: { title: string; lines?: number }) {
  const lines = props.lines ?? 3;
  return (
    <section className="rounded-2xl bg-surface p-6 shadow-md border border-border">
      <div className="h-7 w-40 bg-muted/20 rounded mb-4 animate-pulse" />
      <div className="space-y-2">
        {Array.from({ length: lines }).map((_, i) => (
          <div key={i} className="h-4 w-full bg-muted/10 rounded animate-pulse" />
        ))}
      </div>
    </section>
  );
}

async function AboutSection() {
  try {
    const about = await getAbout();
    return (
      <section id="about" className="scroll-mt-24">
        <SectionHeader title="About" subtitle="Who I am and what I do" />
        <AboutCard about={about as About} />
      </section>
    );
  } catch (e: any) {
    return (
      <section id="about" className="scroll-mt-24">
        <SectionHeader title="About" subtitle="Who I am and what I do" />
        <div className="rounded-xl bg-danger/10 text-danger p-4 border border-danger/30">{e?.message ?? "Failed to load about content."}</div>
      </section>
    );
  }
}

async function ProjectsSection() {
  try {
    const projects = await getProjects();
    return (
      <section id="projects" className="scroll-mt-24">
        <SectionHeader title="Projects" subtitle="Things I've built" />
        {projects.length === 0 ? (
          <div className="rounded-xl bg-muted/10 text-muted p-4 border border-border">No projects published yet.</div>
        ) : (
          <ProjectsGrid projects={projects as Project[]} />
        )}
      </section>
    );
  } catch (e: any) {
    return (
      <section id="projects" className="scroll-mt-24">
        <SectionHeader title="Projects" subtitle="Things I've built" />
        <div className="rounded-xl bg-danger/10 text-danger p-4 border border-danger/30">{e?.message ?? "Failed to load projects."}</div>
      </section>
    );
  }
}

async function SkillsSection() {
  try {
    const skills = await getSkills();
    return (
      <section id="skills" className="scroll-mt-24">
        <SectionHeader title="Skills" subtitle="Technologies I work with" />
