"use client";
import { useState } from "react";
import { postContact } from "../utils/data";
import { Send, Loader2 } from "lucide-react";

export function SectionHeader({ title, subtitle }: { title: string; subtitle?: string }) {
  return (
    <div className="mb-6">
      <h2 className="text-2xl font-bold mb-1">{title}</h2>
      {subtitle ? <p className="text-muted">{subtitle}</p> : null}
    </div>
  );
}

export function ContactForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState<"idle" | "submitting" | "success" | "error">("idle");
  const [error, setError] = useState<string | null>(null);

  const validate = () => {
    if (!name.trim()) return "Name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) return "A valid email is required";
    if (!message.trim() || message.trim().length < 10) return "Please enter a message of at least 10 characters";
    return null;
  };

  const onSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const err = validate();
    if (err) {
      setError(err);
      setStatus("error");
      return;
    }
    setStatus("submitting");
    setError(null);
    try {
      const res = await postContact({ name, email, message });
      if (res.success) {
        setStatus("success");
        setName("");
        setEmail("");
        setMessage("");
      } else {
        setStatus("error");
        setError(res.message || "Something went wrong");
      }
    } catch (e: any) {
      setStatus("error");
      setError(e?.message ?? "Failed to send message");
    }
  };

  return (
    <form onSubmit={onSubmit} className="space-y-4">
      <div className="grid sm:grid-cols-2 gap-4">
        <div className="space-y-2">
          <label htmlFor="name" className="text-sm">Name</label>
          <input id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="Your name" className="w-full rounded-lg border border-border bg-background px-3 py-2 outline-none focus:ring-2 focus:ring-primary" />
        </div>
        <div className="space-y-2">
          <label htmlFor="email" className="text-sm">Email</label>
          <input id="email" name="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" className="w-full rounded-lg border border-border bg-background px-3 py-2 outline-none focus:ring-2 focus:ring-primary" />
        </div>
      </div>
      <div className="space-y-2">
        <label htmlFor="message" className="text-sm">Message</label>
        <textarea id="message" name="message" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="How can I help?" rows={5} className="w-full rounded-lg border border-border bg-background px-3 py-2 outline-none focus:ring-2 focus:ring-primary" />
      </div>
      {status === "error" && error ? (
        <div className="rounded-lg border border-danger/30 bg-danger/10 text-danger px-3 py-2 text-sm">{error}</div>
      ) : null}
      {status === "success" ? (
        <div className="rounded-lg border border-success/30 bg-success/10 text-success px-3 py-2 text-sm">Your message has been sent.</div>
      ) : null}
      <div className="flex items-center gap-3">
        <button type="submit" disabled={status === "submitting"} className="inline-flex items-center gap-2 rounded-lg bg-primary text-white px-4 py-2 text-sm font-medium shadow hover:opacity-90 disabled:opacity-60 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-primary">
          {status === "submitting" ? <Loader2 className="w-4 h-4 animate-spin" /> : <Send className="w-4 h-4" />}<span>Send</span>
        </button>
      </div>
    </form>
  );
}

