import { z } from "zod";

const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL ?? "http://localhost:3001";

const aboutSchema = z.object({
  bio: z.string(),
  email: z.string(),
  location: z.string(),
  name: z.string(),
  social: z.object({ github: z.string(), linkedin: z.string(), twitter: z.string() }),
  title: z.string(),
  updatedAt: z.string(),
});

const projectSchema = z.object({
  createdAt: z.string(),
  description: z.string(),
  featured: z.boolean(),
  id: z.string(),
  images: z.array(z.string()),
  published: z.boolean(),
  repo: z.string(),
  tags: z.array(z.string()),
  title: z.string(),
  updatedAt: z.string(),
  url: z.string(),
});

const skillSchema = z.object({ id: z.string(), level: z.string(), name: z.string(), years: z.number() });

const blogPostSchema = z.object({
  createdAt: z.string(),
  excerpt: z.string(),
  id: z.string(),
  published: z.boolean(),
  publishedAt: z.string().nullable(),
  slug: z.string(),
  tags: z.array(z.string()),
  title: z.string(),
  updatedAt: z.string(),
});

const blogPostDetailSchema = blogPostSchema.extend({ content: z.string() });

const contactResultSchema = z.object({ message: z.string(), success: z.boolean() });

export type About = z.infer<typeof aboutSchema>;
export type Project = z.infer<typeof projectSchema>;
export type Skill = z.infer<typeof skillSchema>;
export type BlogPost = z.infer<typeof blogPostSchema>;
export type BlogPostDetail = z.infer<typeof blogPostDetailSchema>;
export type ContactResult = z.infer<typeof contactResultSchema>;

export type Experience = never;
export type Education = never;
export type Testimonial = never;

async function fetchJSON<T>(path: string, init?: RequestInit & { revalidate?: number }, schema?: z.ZodTypeAny): Promise<T> {
  const url = `${BASE_URL}${path.startsWith("/") ? "" : "/"}${path}`;
  const { revalidate, ...rest } = init || {};
  const options: RequestInit & { next?: { revalidate?: number } } = {
    ...rest,
  };
  if (!rest || !("cache" in rest) || !("next" in rest)) {
    options.next = { revalidate: revalidate ?? 60 };
  }
  const res = await fetch(url, options as RequestInit);
  if (!res.ok) {
    const txt = await res.text().catch(() => "");
    throw new Error(`Request failed ${res.status}: ${txt || res.statusText}`);
  }
  const data = await res.json();
  if (schema) {
    const parsed = schema.safeParse(data);
    if (!parsed.success) {
      throw new Error("Unexpected response shape");
    }
    return parsed.data as T;
  }
  return data as T;
}

export async function getAbout(): Promise<About> {
  try {
    return await fetchJSON<About>("/api/about", { next: { revalidate: 600 } }, aboutSchema);
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch About");
  }
}

export async function getProjects(): Promise<Project[]> {
  try {
    return await fetchJSON<Project[]>("/api/projects", { next: { revalidate: 120 } }, z.array(projectSchema));
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch Projects");
  }
}

export async function getProject(id: number): Promise<Project> {
  try {
    return await fetchJSON<Project>(`/api/projects/${String(id)}`, { next: { revalidate: 120 } }, projectSchema);
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch Project");
  }
}

export async function getSkills(): Promise<Skill[]> {
  try {
    return await fetchJSON<Skill[]>("/api/skills", { next: { revalidate: 600 } }, z.array(skillSchema));
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch Skills");
  }
}

export async function getBlogPosts(): Promise<BlogPost[]> {
  try {
    return await fetchJSON<BlogPost[]>("/api/posts", { next: { revalidate: 120 } }, z.array(blogPostSchema));
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch Blog Posts");
  }
}

export async function getBlogPost(slug: string): Promise<BlogPostDetail> {
  try {
    return await fetchJSON<BlogPostDetail>(`/api/posts/${encodeURIComponent(slug)}`, { next: { revalidate: 120 } }, blogPostDetailSchema);
  } catch (e: any) {
    throw new Error(e?.message || "Failed to fetch Blog Post");
  }
}

export async function getExperience(): Promise<Experience[]> {
  return [] as Experience[];
}

export async function getEducation(): Promise<Education[]> {
  return [] as Education[];
}

export async function getTestimonials(): Promise<Testimonial[]> {
  return [] as Testimonial[];
}

export async function postContact(input: { name: string; email: string; message: string }): Promise<ContactResult> {
  try {
    const res = await fetch(`${BASE_URL}/api/contact`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(input),
    });
    if (!res.ok) {
      const txt = await res.text().catch(() => "");
      throw new Error(txt || "Failed to send message");
    }
    const json = await res.json();
    const parsed = contactResultSchema.safeParse(json);
    if (!parsed.success) {
      throw new Error("Unexpected response shape");
    }
    return parsed.data;
  } catch (e: any) {
    throw new Error(e?.message || "Failed to send message");
  }
}

